import React, { useState, useRef } from 'react';
import ProgressTracker from '../components/ProgressTracker';
import VideoPlayer from '../components/VideoPlayer';

// Simulate a video list (in production, fetch from backend or storage)
const initialVideos = [
  { id: 'lecture1', name: 'Sample Lecture', src: '/sample.mp4' }
];

const Home = () => {
  const [videos, setVideos] = useState(initialVideos);
  const [selectedVideo, setSelectedVideo] = useState(initialVideos[0]);
  const [intervals, setIntervals] = useState([]);
  const [videoDuration, setVideoDuration] = useState(0);
  const fileInputRef = useRef();

  // Handle progress update from VideoPlayer
  const handleProgressUpdate = (newIntervals, duration) => {
    setIntervals(newIntervals);
    setVideoDuration(duration);
    // Refresh progress for the selected video
    setVideoProgress((prev) => {
      if (!duration) return prev;
      // Merge intervals
      let merged = [];
      if (newIntervals.length) {
        newIntervals.sort((a, b) => a.start - b.start);
        merged = [newIntervals[0]];
        for (let i = 1; i < newIntervals.length; i++) {
          const last = merged[merged.length - 1];
          const curr = newIntervals[i];
          if (curr.start <= last.end) {
            last.end = Math.max(last.end, curr.end);
          } else {
            merged.push(curr);
          }
        }
      }
      const uniqueTime = merged.reduce((sum, i) => sum + (i.end - i.start), 0);
      const lastWatched = merged.length ? merged[merged.length - 1].end : 0;
      return {
        ...prev,
        [selectedVideo.id]: {
          percent: duration ? (uniqueTime / duration) * 100 : 0,
          lastWatched,
        }
      };
    });
  };

  // Handle video selection
  const handleSelectVideo = (video) => {
    setSelectedVideo(video);
    setIntervals([]); // Reset intervals, will be loaded by VideoPlayer
    setVideoDuration(0);
  };

  // Handle video upload
  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const url = URL.createObjectURL(file);
    const id = file.name + '-' + Date.now();
    const newVideo = { id, name: file.name, src: url };
    setVideos((prev) => [...prev, newVideo]);
    setSelectedVideo(newVideo);
    setIntervals([]);
    setVideoDuration(0);
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center', margin: 24 }}>Lecture Progress Tracker</h1>
      <div className="container" style={{ marginBottom: 24 }}>
        <div style={{ marginBottom: 12 }}>
          <b>Uploaded Videos:</b>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {videos.map((video) => (
              <li key={video.id} style={{ margin: '8px 0' }}>
                <button
                  style={{
                    background: selectedVideo.id === video.id ? '#76c7c0' : '#eee',
                    color: selectedVideo.id === video.id ? '#fff' : '#333',
                    border: 'none',
                    borderRadius: 6,
                    padding: '6px 16px',
                    cursor: 'pointer',
                    marginRight: 8
                  }}
                  onClick={() => handleSelectVideo(video)}
                >
                  {video.name}
                </button>
              </li>
            ))}
          </ul>
        </div>
        <input
          type="file"
          accept="video/*"
          ref={fileInputRef}
          style={{ display: 'none' }}
          onChange={handleUpload}
        />
        <button
          onClick={() => fileInputRef.current.click()}
          style={{
            background: '#4fa3a0',
            color: '#fff',
            border: 'none',
            borderRadius: 6,
            padding: '8px 18px',
            cursor: 'pointer'
          }}
        >
          Upload Video
        </button>
      </div>
      <VideoPlayer
        key={selectedVideo.id}
        videoId={selectedVideo.id}
        videoSrc={selectedVideo.src}
        onProgressUpdate={handleProgressUpdate}
      />
      <ProgressTracker intervals={intervals} videoDuration={videoDuration} />
    </div>
  );
};

export default Home;
