import React, { useState, useEffect } from 'react';
import VideoPlayer from '../components/VideoPlayer';
import { fetchProgress } from '../api/progressApi';

const USER_ID = 'user1'; // Should match VideoPlayer

const Home = () => {
  const [videos, setVideos] = useState([
    // Example video objects
    // { id: 'video1', name: 'Lecture 1', url: '...' }
  ]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [intervals, setIntervals] = useState([]);
  const [videoDuration, setVideoDuration] = useState(0);
  const [videoProgress, setVideoProgress] = useState({}); // { videoId: { percent, lastWatched } }

  // Fetch progress for all videos on mount or when videos change
  useEffect(() => {
    const fetchAllProgress = async () => {
      const progressMap = {};
      for (const video of videos) {
        try {
          const data = await fetchProgress(USER_ID, video.id);
          const intervals = data.intervals || [];
          if (intervals.length) {
            // Merge intervals and get last watched
            intervals.sort((a, b) => a.start - b.start);
            let merged = [intervals[0]];
            for (let i = 1; i < intervals.length; i++) {
              const last = merged[merged.length - 1];
              const curr = intervals[i];
              if (curr.start <= last.end) {
                last.end = Math.max(last.end, curr.end);
              } else {
                merged.push(curr);
              }
            }
            const uniqueTime = merged.reduce((sum, i) => sum + (i.end - i.start), 0);
            // You may want to fetch duration from your backend or store it elsewhere
            // For now, percent will be 0 until duration is available
            progressMap[video.id] = {
              percent: 0,
              lastWatched: merged[merged.length - 1].end,
              mergedIntervals: merged,
              uniqueTime
            };
          }
        } catch {
          // No progress
        }
      }
      setVideoProgress(progressMap);
    };
    fetchAllProgress();
  }, [videos]);

  // Handle progress update from VideoPlayer
  const handleProgressUpdate = (newIntervals, duration) => {
    setIntervals(newIntervals);
    setVideoDuration(duration);
    // Refresh progress for the selected video
    setVideoProgress((prev) => {
      if (!duration) return prev;
      // Merge intervals
      let merged = [];
      if (newIntervals.length) {
        newIntervals.sort((a, b) => a.start - b.start);
        merged = [newIntervals[0]];
        for (let i = 1; i < newIntervals.length; i++) {
          const last = merged[merged.length - 1];
          const curr = newIntervals[i];
          if (curr.start <= last.end) {
            last.end = Math.max(last.end, curr.end);
          } else {
            merged.push(curr);
          }
        }
      }
      const uniqueTime = merged.reduce((sum, i) => sum + (i.end - i.start), 0);
      const lastWatched = merged.length ? merged[merged.length - 1].end : 0;
      return {
        ...prev,
        [selectedVideo.id]: {
          percent: duration ? (uniqueTime / duration) * 100 : 0,
          lastWatched,
        }
      };
    });
  };

  const handleSelectVideo = (video) => {
    setSelectedVideo(video);
    // Optionally fetch intervals and duration for this video
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center', margin: 24 }}>Lecture Progress Tracker</h1>
      <div className="container" style={{ marginBottom: 24 }}>
        <div style={{ marginBottom: 12 }}>
          <b>Uploaded Videos:</b>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {videos.map((video) => {
              const progress = videoProgress[video.id];
              let percent = 0;
              if (progress && videoDuration && selectedVideo && selectedVideo.id === video.id) {
                percent = (progress.uniqueTime || 0) / videoDuration * 100;
              } else if (progress && progress.percent) {
                percent = progress.percent;
              }
              return (
                <li key={video.id} style={{ margin: '8px 0' }}>
                  <button
                    style={{
                      background: selectedVideo && selectedVideo.id === video.id ? '#76c7c0' : '#eee',
                      color: selectedVideo && selectedVideo.id === video.id ? '#fff' : '#333',
                      border: 'none',
                      borderRadius: 6,
                      padding: '6px 16px',
                      cursor: 'pointer',
                      marginRight: 8
                    }}
                    onClick={() => handleSelectVideo(video)}
                  >
                    {video.name}
                  </button>
                  {percent > 0 && (
                    <span style={{ marginLeft: 8, color: '#4fa3a0', fontWeight: 500 }}>
                      {percent.toFixed(1)}% watched
                      {percent < 100 && (
                        <span style={{ marginLeft: 8, color: '#888' }}>â€¢ Continue Watching</span>
                      )}
                    </span>
                  )}
                </li>
              );
            })}
          </ul>
        </div>
        {selectedVideo && (
          <VideoPlayer
            video={selectedVideo}
            userId={USER_ID}
            onProgressUpdate={handleProgressUpdate}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
